import { createContext } from 'react'

export const ThemeContext = createContext() 

// on the context object is a context provider component which we can then access 

export default function ThemeProvider({ children }) { //represents children components 

    return (
        <ThemeContext.Provider value={{color: 'blue'}}>
            {children}
        </ThemeContext.Provider>
    )
}
